import React from 'react';

const ResultDisplay = ({ maskedImageUrl, onDownload, onReset }) => {
  if (!maskedImageUrl) {
    return null;
  }

  const handleDownload = () => {
    const link = document.createElement('a');
    link.href = maskedImageUrl;
    link.download = `masked-image-${Date.now()}.png`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    if (onDownload) {
      onDownload();
    }
  };

  return (
    <div className="w-full max-w-2xl mx-auto">
      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        <div className="flex items-center justify-between p-4 bg-green-50 border-b">
          <h3 className="text-lg font-medium text-green-900 flex items-center">
            <svg
              className="w-5 h-5 mr-2 text-green-600"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z"
              />
            </svg>
            Masked Image Result
          </h3>
          <div className="flex space-x-2">
            <button
              onClick={handleDownload}
              className="px-3 py-1 bg-blue-600 text-white text-sm rounded-md hover:bg-blue-700 transition-colors duration-200 flex items-center"
            >
              <svg
                className="w-4 h-4 mr-1"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M12 10v6m0 0l-3-3m3 3l3-3m2 8H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
                />
              </svg>
              Download
            </button>
            {onReset && (
              <button
                onClick={onReset}
                className="px-3 py-1 bg-gray-600 text-white text-sm rounded-md hover:bg-gray-700 transition-colors duration-200"
              >
                New Image
              </button>
            )}
          </div>
        </div>

        <div className="p-4">
          <div className="relative">
            <img
              src={maskedImageUrl}
              alt="Masked result"
              className="w-full h-auto max-h-96 object-contain rounded-md border border-gray-200"
            />
          </div>

          <div className="mt-4 p-3 bg-green-50 rounded-md">
            <p className="text-sm text-green-800">
              <span className="font-medium">âœ“ Processing Complete:</span> PII data has been successfully masked in your image.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ResultDisplay;
