import React from 'react';

const ImagePreview = ({ imageFile, imageUrl, onRemove, title = "Original Image" }) => {
  const displayUrl = imageUrl || (imageFile ? URL.createObjectURL(imageFile) : null);

  if (!displayUrl) {
    return null;
  }

  return (
    <div className="w-full max-w-2xl mx-auto">
      <div className="bg-white rounded-lg shadow-md overflow-hidden">
        <div className="flex items-center justify-between p-4 bg-gray-50 border-b">
          <h3 className="text-lg font-medium text-gray-900">{title}</h3>
          {onRemove && (
            <button
              onClick={onRemove}
              className="text-gray-400 hover:text-red-500 transition-colors duration-200"
              title="Remove image"
            >
              <svg
                className="w-5 h-5"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M6 18L18 6M6 6l12 12"
                />
              </svg>
            </button>
          )}
        </div>

        <div className="p-4">
          <div className="relative">
            <img
              src={displayUrl}
              alt="Preview"
              className="w-full h-auto max-h-96 object-contain rounded-md border border-gray-200"
            />
          </div>

          {imageFile && (
            <div className="mt-3 text-sm text-gray-500">
              <p><span className="font-medium">File:</span> {imageFile.name}</p>
              <p><span className="font-medium">Size:</span> {(imageFile.size / 1024 / 1024).toFixed(2)} MB</p>
              <p><span className="font-medium">Type:</span> {imageFile.type}</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ImagePreview;
