import React, { useState, useCallback } from 'react';
import ImageUploader from './components/ImageUploader';
import ImagePreview from './components/ImagePreview';
import ResultDisplay from './components/ResultDisplay';
import ProgressBar from './components/ProgressBar';
import Toast from './components/Toast';
import { maskImage } from './services/apiService';

function App() {
  const [uploadedImage, setUploadedImage] = useState(null);
  const [maskedImageUrl, setMaskedImageUrl] = useState(null);
  const [detectedPII, setDetectedPII] = useState(null);
  const [maskingStyle, setMaskingStyle] = useState('blackbar');
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState(null);
  const [toast, setToast] = useState(null);
  const [processingStep, setProcessingStep] = useState('');

  const handleImageUpload = useCallback((file) => {
    setUploadedImage(file);
    setMaskedImageUrl(null);
    setDetectedPII(null);
    setError(null);
    setToast({ message: 'Image uploaded successfully!', type: 'success' });
  }, []);

  const handleRemoveImage = useCallback(() => {
    setUploadedImage(null);
    setMaskedImageUrl(null);
    setDetectedPII(null);
    setError(null);
    setToast(null);
  }, []);

  const handleProcessImage = useCallback(async () => {
    if (!uploadedImage) return;

    setIsProcessing(true);
    setError(null);
    setToast(null);

    try {
      const result = await maskImage(uploadedImage, maskingStyle);

      if (result.success) {
        setMaskedImageUrl(result.maskedImageUrl);
        setDetectedPII(result.detectedPII);
        setToast({ 
          message: 'Image processed successfully! PII data has been masked.', 
          type: 'success' 
        });
      } else {
        throw new Error(result.message || 'Processing failed');
      }
    } catch (err) {
      setError(err.message);
      setToast({ message: err.message, type: 'error' });
    } finally {
      setIsProcessing(false);
      setProcessingStep('');
    }
  }, [uploadedImage, maskingStyle]);

  const handleReset = useCallback(() => {
    setUploadedImage(null);
    setMaskedImageUrl(null);
    setDetectedPII(null);
    setError(null);
    setIsProcessing(false);
    setProcessingStep('');
    setToast(null);
  }, []);

  const handleDownload = useCallback(() => {
    setToast({ message: 'Image downloaded successfully!', type: 'success' });
  }, []);

  const handleStyleChange = useCallback(async (newStyle) => {
    if (!uploadedImage || newStyle === maskingStyle) return;

    setMaskingStyle(newStyle);
    setIsProcessing(true);
    setError(null);

    try {
      const result = await maskImage(uploadedImage, newStyle);

      if (result.success) {
        setMaskedImageUrl(result.maskedImageUrl);
        setDetectedPII(result.detectedPII);
        setToast({ 
          message: `Masking style changed to ${newStyle}!`, 
          type: 'success' 
        });
      }
    } catch (err) {
      setError(err.message);
      setToast({ message: err.message, type: 'error' });
    } finally {
      setIsProcessing(false);
    }
  }, [uploadedImage, maskingStyle]);

  const closeToast = useCallback(() => {
    setToast(null);
  }, []);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-4xl mx-auto px-4 py-6">
          <div className="text-center">
            <h1 className="text-3xl font-bold text-gray-900">PII Image Masking Tool</h1>
            <p className="mt-2 text-gray-600">
              Automatically detect and mask personally identifiable information in your images
            </p>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-4xl mx-auto px-4 py-8">
        <div className="space-y-8">

          {/* Error Display */}
          {error && (
            <div className="bg-red-50 border border-red-200 rounded-lg p-4">
              <div className="flex items-center">
                <svg
                  className="w-5 h-5 text-red-600 mr-2"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                  />
                </svg>
                <div>
                  <h3 className="text-sm font-medium text-red-800">Processing Error</h3>
                  <p className="text-sm text-red-700 mt-1">{error}</p>
                </div>
              </div>
            </div>
          )}

          {/* Step 1: Image Upload */}
          {!uploadedImage && !isProcessing && !maskedImageUrl && (
            <div className="text-center">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">
                Step 1: Upload Your Image
              </h2>
              <ImageUploader onImageUpload={handleImageUpload} disabled={isProcessing} />
            </div>
          )}

          {/* Step 2: Image Preview & Process */}
          {uploadedImage && !isProcessing && !maskedImageUrl && (
            <div className="space-y-6">
              <div className="text-center">
                <h2 className="text-xl font-semibold text-gray-900 mb-4">
                  Step 2: Review & Process
                </h2>
              </div>

              <ImagePreview 
                imageFile={uploadedImage} 
                onRemove={handleRemoveImage}
                title="Original Image"
              />

              <div className="text-center">
                <button
                  onClick={handleProcessImage}
                  className="px-8 py-3 bg-blue-600 text-white font-medium rounded-lg hover:bg-blue-700 transition-colors duration-200 shadow-md hover:shadow-lg"
                >
                  Process Image for PII
                </button>
                <p className="text-sm text-gray-500 mt-2">
                  This will analyze your image and mask any detected PII
                </p>
              </div>
            </div>
          )}

          {/* Step 3: Processing */}
          {isProcessing && (
            <div className="text-center">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">
                Step 3: Processing Your Image
              </h2>
              <Loader 
                message={processingStep || "Processing your image..."} 
                subMessage="Please wait while we detect and mask PII data"
              />
            </div>
          )}

          {/* Step 4: Results */}
          {maskedImageUrl && !isProcessing && (
            <div className="space-y-6">
              <div className="text-center">
                <h2 className="text-xl font-semibold text-gray-900 mb-4">
                  Step 4: Download Your Masked Image
                </h2>
              </div>

              <ResultDisplay 
                maskedImageUrl={maskedImageUrl}
                onDownload={handleDownload}
                onReset={handleReset}
              />

              {/* Original vs Masked Comparison */}
              {uploadedImage && (
                <div className="grid md:grid-cols-2 gap-6">
                  <ImagePreview 
                    imageFile={uploadedImage} 
                    title="Original Image"
                  />
                  <ImagePreview 
                    imageUrl={maskedImageUrl} 
                    title="Masked Image"
                  />
                </div>
              )}
            </div>
          )}
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-white border-t mt-16">
        <div className="max-w-4xl mx-auto px-4 py-6">
          <div className="text-center text-sm text-gray-500">
            <p>
              This tool helps protect privacy by automatically masking PII in images.
              <br />
              All processing is done securely and no images are stored permanently.
            </p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
