import React, { useState, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { ThemeProvider } from './contexts/ThemeContext';
import ImageUploader from './components/ImageUploader';
import ImagePreview from './components/ImagePreview';
import ResultDisplay from './components/ResultDisplay';
import ProgressBar from './components/ProgressBar';
import Toast from './components/Toast';
import DarkModeToggle from './components/DarkModeToggle';
import ConfirmationModal from './components/ConfirmationModal';
import { maskImage } from './services/apiService';

const AppContent = () => {
  const [uploadedImage, setUploadedImage] = useState(null);
  const [maskedImageUrl, setMaskedImageUrl] = useState(null);
  const [detectedPII, setDetectedPII] = useState(null);
  const [maskingStyle, setMaskingStyle] = useState('blackbar');
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState(null);
  const [toast, setToast] = useState(null);
  const [showConfirmation, setShowConfirmation] = useState(false);
  const [processingStep, setProcessingStep] = useState('');

  const handleImageUpload = useCallback((file) => {
    setUploadedImage(file);
    setMaskedImageUrl(null);
    setDetectedPII(null);
    setError(null);
    setToast({ message: 'Image uploaded successfully!', type: 'success' });
  }, []);

  const handleRemoveImage = useCallback(() => {
    setUploadedImage(null);
    setMaskedImageUrl(null);
    setDetectedPII(null);
    setError(null);
    setToast(null);
  }, []);

  const handleProcessImage = useCallback(async () => {
    if (!uploadedImage) return;

    setIsProcessing(true);
    setError(null);
    setToast(null);

    try {
      const result = await maskImage(uploadedImage, maskingStyle);

      if (result.success) {
        setMaskedImageUrl(result.maskedImageUrl);
        setDetectedPII(result.detectedPII);
        setShowConfirmation(true);
        setToast({ 
          message: 'Image processed successfully! PII data has been masked.', 
          type: 'success' 
        });
      } else {
        throw new Error(result.message || 'Processing failed');
      }
    } catch (err) {
      setError(err.message);
      setToast({ message: err.message, type: 'error' });
    } finally {
      setIsProcessing(false);
      setProcessingStep('');
    }
  }, [uploadedImage, maskingStyle]);

  const handleReset = useCallback(() => {
    setUploadedImage(null);
    setMaskedImageUrl(null);
    setDetectedPII(null);
    setError(null);
    setIsProcessing(false);
    setProcessingStep('');
    setToast(null);
  }, []);

  const handleDownload = useCallback(() => {
    setToast({ message: 'Image downloaded successfully!', type: 'success' });
  }, []);

  const handleStyleChange = useCallback(async (newStyle) => {
    if (!uploadedImage || newStyle === maskingStyle) return;

    setMaskingStyle(newStyle);
    setIsProcessing(true);
    setError(null);

    try {
      const result = await maskImage(uploadedImage, newStyle);

      if (result.success) {
        setMaskedImageUrl(result.maskedImageUrl);
        setDetectedPII(result.detectedPII);
        setToast({ 
          message: `Masking style changed to ${newStyle}!`, 
          type: 'success' 
        });
      }
    } catch (err) {
      setError(err.message);
      setToast({ message: err.message, type: 'error' });
    } finally {
      setIsProcessing(false);
    }
  }, [uploadedImage, maskingStyle]);

  const closeToast = useCallback(() => {
    setToast(null);
  }, []);

  return (
    <div className="min-h-screen font-inter">
      {/* Sticky Header */}
      <motion.header 
        className="sticky top-0 z-40 backdrop-blur-lg bg-white/80 dark:bg-gray-900/80 border-b border-gray-200/50 dark:border-gray-700/50"
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ type: "spring", stiffness: 300, damping: 30 }}
      >
        <div className="max-w-7xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <motion.div 
              className="flex items-center space-x-3"
              whileHover={{ scale: 1.02 }}
            >
              <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-purple-600 rounded-xl flex items-center justify-center">
                <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
                </svg>
              </div>
              <div>
                <h1 className="text-xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                  PII Shield
                </h1>
                <p className="text-xs text-gray-500 dark:text-gray-400">AI Privacy Protection</p>
              </div>
            </motion.div>

            <div className="flex items-center space-x-4">
              <DarkModeToggle />
            </div>
          </div>
        </div>
      </motion.header>

      {/* Toast Notifications */}
      {toast && (
        <Toast
          message={toast.message}
          type={toast.type}
          onClose={closeToast}
        />
      )}

      {/* Main Content */}
      <main className="max-w-6xl mx-auto px-4 py-8">
        <div className="space-y-8">

          {/* Step 1: Image Upload */}
          {!uploadedImage && !isProcessing && !maskedImageUrl && (
            <div className="text-center">
              <div className="mb-8">
                <h2 className="text-2xl font-bold text-gray-900 mb-2">
                  Step 1: Upload Your Image
                </h2>
                <p className="text-gray-600">
                  Select an image to automatically detect and mask PII data
                </p>
              </div>
              <ImageUploader onImageUpload={handleImageUpload} disabled={isProcessing} />
            </div>
          )}

          {/* Step 2: Image Preview & Process */}
          {uploadedImage && !isProcessing && !maskedImageUrl && (
            <div className="space-y-8">
              <div className="text-center">
                <h2 className="text-2xl font-bold text-gray-900 mb-2">
                  Step 2: Review & Process
                </h2>
                <p className="text-gray-600">
                  Review your image and choose a masking style
                </p>
              </div>

              <ImagePreview 
                imageFile={uploadedImage} 
                onRemove={handleRemoveImage}
                title="Original Image"
              />

              {/* Masking Style Selection */}
              <div className="max-w-2xl mx-auto">
                <div className="bg-white rounded-xl shadow-lg p-6 border border-gray-100">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                    <svg className="w-5 h-5 mr-2 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 21a4 4 0 01-4-4V5a2 2 0 012-2h4a2 2 0 012 2v12a4 4 0 01-4 4zM21 5a2 2 0 00-2-2h-4a2 2 0 00-2 2v12a4 4 0 004 4h4a2 2 0 002-2V5z" />
                    </svg>
                    Choose Masking Style
                  </h3>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
                    {[
                      { id: 'blackbar', name: 'Black Bar', desc: 'Solid black rectangles with text' },
                      { id: 'blur', name: 'Blur', desc: 'Gaussian blur effect' },
                      { id: 'pixelate', name: 'Pixelate', desc: 'Pixelated mosaic effect' }
                    ].map((style) => (
                      <button
                        key={style.id}
                        onClick={() => setMaskingStyle(style.id)}
                        className={`p-4 rounded-lg border-2 transition-all duration-200 text-left ${
                          maskingStyle === style.id
                            ? 'border-blue-500 bg-blue-50 shadow-md'
                            : 'border-gray-200 hover:border-gray-300 hover:bg-gray-50'
                        }`}
                      >
                        <div className="font-medium text-gray-900">{style.name}</div>
                        <div className="text-sm text-gray-600 mt-1">{style.desc}</div>
                      </button>
                    ))}
                  </div>

                  <div className="text-center">
                    <button
                      onClick={handleProcessImage}
                      className="px-8 py-3 bg-gradient-to-r from-blue-600 to-blue-700 text-white font-semibold rounded-lg hover:from-blue-700 hover:to-blue-800 transition-all duration-200 shadow-lg hover:shadow-xl transform hover:-translate-y-0.5"
                    >
                      ðŸ”’ Process Image for PII
                    </button>
                    <p className="text-sm text-gray-500 mt-3">
                      AI will analyze your image and mask detected PII using <span className="font-medium">{maskingStyle}</span> style
                    </p>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Step 3: Processing */}
          {isProcessing && (
            <div className="text-center">
              <div className="mb-8">
                <h2 className="text-2xl font-bold text-gray-900 mb-2">
                  Step 3: AI Processing
                </h2>
                <p className="text-gray-600">
                  Our AI is analyzing your image and applying privacy protection
                </p>
              </div>
              <ProgressBar 
                duration={3000}
                steps={[
                  "Uploading image securely...",
                  "Detecting faces and identities...",
                  "Analyzing text regions...",
                  "Applying privacy masks...",
                  "Finalizing protected image..."
                ]}
              />
            </div>
          )}

          {/* Step 4: Results */}
          {maskedImageUrl && !isProcessing && (
            <div className="space-y-8">
              <div className="text-center">
                <h2 className="text-2xl font-bold text-gray-900 mb-2">
                  Step 4: Privacy Protection Complete
                </h2>
                <p className="text-gray-600">
                  Your image has been processed and PII data has been masked
                </p>
              </div>

              <ResultDisplay 
                maskedImageUrl={maskedImageUrl}
                detectedPII={detectedPII}
                maskingStyle={maskingStyle}
                onDownload={handleDownload}
                onReset={handleReset}
                onStyleChange={handleStyleChange}
              />

              {/* Original vs Masked Comparison */}
              {uploadedImage && (
                <div className="bg-white rounded-xl shadow-lg p-6 border border-gray-100">
                  <h3 className="text-xl font-semibold text-gray-900 mb-6 text-center flex items-center justify-center">
                    <svg className="w-6 h-6 mr-2 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                    </svg>
                    Before & After Comparison
                  </h3>
                  <div className="grid md:grid-cols-2 gap-6">
                    <ImagePreview 
                      imageFile={uploadedImage} 
                      title="ðŸ”“ Original Image"
                    />
                    <ImagePreview 
                      imageUrl={maskedImageUrl} 
                      title="ðŸ”’ Protected Image"
                    />
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      </main>

      {/* Footer */}
      <footer className="bg-gradient-to-r from-gray-50 to-slate-50 border-t mt-16">
        <div className="max-w-6xl mx-auto px-4 py-8">
          <div className="text-center">
            <div className="flex justify-center items-center mb-4">
              <svg className="w-8 h-8 text-blue-600 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 15v2m-6 4h12a2 2 0 002-2v-6a2 2 0 00-2-2H6a2 2 0 00-2 2v6a2 2 0 002 2zm10-10V7a4 4 0 00-8 0v4h8z" />
              </svg>
              <h3 className="text-lg font-semibold text-gray-900">Privacy-First AI Protection</h3>
            </div>
            <div className="grid md:grid-cols-3 gap-6 text-sm text-gray-600 mb-6">
              <div className="flex items-start justify-center">
                <svg className="w-5 h-5 text-green-600 mr-2 mt-0.5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                </svg>
                <div>
                  <div className="font-medium text-gray-900">Secure Processing</div>
                  <div>All processing happens locally in your browser</div>
                </div>
              </div>
              <div className="flex items-start justify-center">
                <svg className="w-5 h-5 text-green-600 mr-2 mt-0.5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                </svg>
                <div>
                  <div className="font-medium text-gray-900">No Data Storage</div>
                  <div>Images are never stored or transmitted to servers</div>
                </div>
              </div>
              <div className="flex items-start justify-center">
                <svg className="w-5 h-5 text-green-600 mr-2 mt-0.5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                </svg>
                <div>
                  <div className="font-medium text-gray-900">AI-Powered Detection</div>
                  <div>Advanced algorithms detect faces, text, and PII</div>
                </div>
              </div>
            </div>
            <p className="text-xs text-gray-500">
              Built with React, Tailwind CSS, and Canvas API â€¢ Designed for privacy and security
            </p>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
